import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import store from './store';
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap";
import "bootstrap/dist/js/bootstrap.min.js";
import "./assets/scss/app.scss";
import VueFeather from "vue-feather";
import Breadcrumbs from './layout/breadCrumbs.vue';

// Agregar lógica para verificar el token antes de cada cambio de ruta
router.beforeEach((to, from, next) => {
  // Verifica si la ruta requiere autenticación
  if (to.matched.some(record => record.meta.requiresAuth)) {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No se encontró ningún token, redireccionando para iniciar sesión');
      next('/login'); // Redirecciona a la página de inicio de sesión si no hay token
    } else {
      next(); // Si hay token, permite la navegación
    }
  } else {
    next(); // Si la ruta no requiere autenticación, permite la navegación
  }
});

createApp(App)
  .use(store)
  .use(router)
  .component(VueFeather.name, VueFeather)
  .component('Breadcrumbs', Breadcrumbs)
  .mount('#app');
