import { createStore } from 'vuex'
import menu from "./modules/menu"
import layout from "./modules/layout"
export default createStore({
  state: {
    token: localStorage.getItem('token') || null,
    userType: localStorage.getItem('userType') || null, // 'pet_owner' o 'business_owner'
  },

  mutations: {
    SET_AUTH(state, { token, userType }) {
      state.token = token;
      state.userType = userType;
     },
    CLEAR_AUTH(state) {
      state.token = null;
      state.userType = null;
      },
  },
  actions: {
    // login({ commit }, { token, userType }) {
    //   localStorage.setItem('token', token);
    //   localStorage.setItem('userType', userType);
    //   commit('SET_AUTH', { token, userType });
    // },
    logout({ state, commit }) {
      return new Promise((resolve, reject) => {
        var myHeaders = new Headers();
        let tokenBearer = "Bearer " + localStorage.getItem("token");
        myHeaders.append("Authorization", tokenBearer.toString());

        let url = '';
        if (state.userType === 'pet_owner') {
          url = "https://devs-devitm.com/api/pet_owner/logout";
        } else if (state.userType === 'business_owner') {
          url = "https://devs-devitm.com/api/business_owner/logout";
        }

        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          redirect: 'follow',
        };

        fetch(url, requestOptions)
          .then(response => response.text())
          .then(result => {
            let resultado = JSON.parse(result);
            if (resultado.status === "success") {
              localStorage.removeItem('token');
              localStorage.removeItem('userType');
              commit('CLEAR_AUTH');
              resolve();
            } else {
              reject(new Error('Error al cerrar sesión'));
            }
          })
          .catch(error => reject(error));
      });
    },

  },
  getters: {
    isAuthenticated: state => !!state.token,
    isPetOwner: state => state.userType === 'pet_owner',
    isBusinessOwner: state => state.userType === 'business_owner',
  },
  modules: {
    menu,
    layout
  }
})
