import { createRouter, createWebHistory } from 'vue-router'
import BodyView from "@/layout/BodyView.vue"
import indexDashbord from "@/pages/indexDashbord.vue"
import login from "@/auth/login.vue"
import Register from "../components/register.vue"
import ForgetPassword from "../components/forgetpassword.vue"
import Sesion from "@/pages/sesionVue.vue"
import indexUser from "@/auth/indexUser.vue"

const routes = [
  {
    path: '/auth/indexUser',
    name: 'indexUser',
    component: indexUser
  },
  {
    path: '/Sesion',
    name: 'Sesion',
    component: Sesion
  },
  {
    path: '/components/forgetpassword',
    name: 'ForgetPassword',
    component: ForgetPassword
  },
  {
    path: '/auth/register',
    name: 'Register',
    component: Register
  },
  {
    path:"/auth/login",
    name:"login",
    component:login,
    meta:{
      title:'login-Tivo Starter-kit'
    }
  },
 {
   path:"/",
   component:BodyView,
   children:[
     {
       path:"dashboard",
       name:"dashbord",
       component:indexDashbord,
       meta: {
        title: 'Tivo Starter-kit - Premium Vue Admin Template',
      }
     }
   ]
 }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
});
router.beforeEach((to, from, next) => {
  // Verificar si la ruta requiere autenticación
  if (to.matched.some(record => record.meta.requiresAuth)) {
    const loggedIn = localStorage.getItem('token');

    if (!loggedIn) {
      // Si no hay token, redirigir al login
      next({ name: 'login' });
    } else {
      next({name:'indexUser'}); // Continuar navegación
    }
  } else {
    next(); //  continuar navegación
  }
});

export default router;
